        // --- PLACEHOLDERS FOR MISSING MONTHS (JULY-NOV) ---

        public string[] Placeholder = {
            "\n\n\n",
            "                  [ ARTWORK COMING SOON ]                  ",
            "\n\n\n"
        };

        // --- DRAWING METHOD ---

        public void DrawMonthArt(int monthIndex)
        {
            string[] selectedMonth;

            switch (monthIndex)
            {
                case 0: selectedMonth = January; break;
                case 1: selectedMonth = February; break;
                case 2: selectedMonth = March; break;
                case 3: selectedMonth = April; break;
                case 4: selectedMonth = May; break;
                case 5: selectedMonth = June; break;
                // For Jul-Nov, we use placeholder until you provide art
                case 6: selectedMonth = Placeholder; break; // July
                case 7: selectedMonth = Placeholder; break; // Aug
                case 8: selectedMonth = Placeholder; break; // Sept
                case 9: selectedMonth = Placeholder; break; // Oct
                case 10: selectedMonth = Placeholder; break; // Nov
                case 11: selectedMonth = December; break;
                default: selectedMonth = January; break;
            }

            Console.ForegroundColor = ConsoleColor.White;
            foreach (string line in selectedMonth)
            {
                // Simple centering or just raw printing
                int padding = Math.Max(0, (Console.WindowWidth - line.Length) / 2);
                Console.WriteLine(new string(' ', padding) + line);
            }
            Console.ResetColor();
        }
